use super::super::{Fix, Report};
use crate::model::TaskResult;
use std::path::Path;

fn task_result(file_name: &str) -> TaskResult {
    TaskResult::from(
        std::fs::read(
            Path::new(env!("CARGO_MANIFEST_DIR"))
                .join("tests/fixtures")
                .join(format!("{}.rmp", file_name)),
        )
        .unwrap()
        .as_slice(),
    )
}

